(4987, 1, 56, 8, 'فَأَصْحَابُ الْمَيْمَنَةِ مَا أَصْحَابُ الْمَيْمَنَةِ'),
(4988, 1, 56, 9, 'وَأَصْحَابُ الْمَشْأَمَةِ مَا أَصْحَابُ الْمَشْأَمَةِ'),
(4989, 1, 56, 10, 'وَالسَّابِقُونَ السَّابِقُونَ'),
(4990, 1, 56, 11, 'أُولَٰئِكَ الْمُقَرَّبُونَ'),
(4991, 1, 56, 12, 'فِي جَنَّاتِ النَّعِيمِ'),
(4992, 1, 56, 13, 'ثُلَّةٌ مِنَ الْأَوَّلِينَ'),
(4993, 1, 56, 14, 'وَقَلِيلٌ مِنَ الْآخِرِينَ'),
(4994, 1, 56, 15, 'عَلَىٰ سُرُرٍ مَوْضُونَةٍ'),
(4995, 1, 56, 16, 'مُتَّكِئِينَ عَلَيْهَا مُتَقَابِلِينَ'),
(4996, 1, 56, 17, 'يَطُوفُ عَلَيْهِمْ وِلْدَانٌ مُخَلَّدُونَ'),
(4997, 1, 56, 18, 'بِأَكْوَابٍ وَأَبَارِيقَ وَكَأْسٍ مِنْ مَعِينٍ'),
(4998, 1, 56, 19, 'لَا يُصَدَّعُونَ عَنْهَا وَلَا يُنْزِفُونَ'),
(4999, 1, 56, 20, 'وَفَاكِهَةٍ مِمَّا يَتَخَيَّرُونَ'),
(5000, 1, 56, 21, 'وَلَحْمِ طَيْرٍ مِمَّا يَشْتَهُونَ'),
(5001, 1, 56, 22, 'وَحُورٌ عِينٌ'),
(5002, 1, 56, 23, 'كَأَمْثَالِ اللُّؤْلُؤِ الْمَكْنُونِ'),
(5003, 1, 56, 24, 'جَزَاءً بِمَا كَانُوا يَعْمَلُونَ'),
(5004, 1, 56, 25, 'لَا يَسْمَعُونَ فِيهَا لَغْوًا وَلَا تَأْثِيمًا'),
(5005, 1, 56, 26, 'إِلَّا قِيلًا سَلَامًا سَلَامًا'),
(5006, 1, 56, 27, 'وَأَصْحَابُ الْيَمِينِ مَا أَصْحَابُ الْيَمِينِ'),
(5007, 1, 56, 28, 'فِي سِدْرٍ مَخْضُودٍ'),
(5008, 1, 56, 29, 'وَطَلْحٍ مَنْضُودٍ'),
(5009, 1, 56, 30, 'وَظِلٍّ مَمْدُودٍ'),
(5010, 1, 56, 31, 'وَمَاءٍ مَسْكُوبٍ'),
(5011, 1, 56, 32, 'وَفَاكِهَةٍ كَثِيرَةٍ'),
(5012, 1, 56, 33, 'لَا مَقْطُوعَةٍ وَلَا مَمْنُوعَةٍ'),
(5013, 1, 56, 34, 'وَفُرُشٍ مَرْفُوعَةٍ'),
(5014, 1, 56, 35, 'إِنَّا أَنْشَأْنَاهُنَّ إِنْشَاءً'),
(5015, 1, 56, 36, 'فَجَعَلْنَاهُنَّ أَبْكَارًا'),
(5016, 1, 56, 37, 'عُرُبًا أَتْرَابًا'),
(5017, 1, 56, 38, 'لِأَصْحَابِ الْيَمِينِ'),
(5018, 1, 56, 39, 'ثُلَّةٌ مِنَ الْأَوَّلِينَ'),
(5019, 1, 56, 40, 'وَثُلَّةٌ مِنَ الْآخِرِينَ'),
(5020, 1, 56, 41, 'وَأَصْحَابُ الشِّمَالِ مَا أَصْحَابُ الشِّمَالِ'),
